import React from 'react';
import ReactDOM from 'react-dom';
import { unstable_concurrentAct } from 'react-dom/cjs/react-dom-test-utils.production.min';
import './index.css';

function Square(props){
    return (
    <button className="square" onClick={props.onClick}>
        {props.value}
    </button>
    );
}

class Board extends React.Component{
    renderSquare(i){
        return <Square value={this.props.squares[i]}
                        onClick={()=>this.props.onClick(i)}/>
    }
    render(){
        return (
            <div>
                <div className="board-row">{this.renderSquare(0)}{this.renderSquare(1)}{this.renderSquare(2)}</div>
                <div className="board-row">{this.renderSquare(3)}{this.renderSquare(4)}{this.renderSquare(5)}</div>
                <div className="board-row">{this.renderSquare(6)}{this.renderSquare(7)}{this.renderSquare(8)}</div>
            </div>
        )
    }
}

function calculateWinner(squares){
    const lines=[
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6],
    ];
    for(let i=0;i<lines.length;i++){
        let [a, b, c]=lines[i];
        if(squares[a] && squares[a]==squares[b] && squares[a]==squares[c]) return squares[a];
    }
    return null;
}

class Game extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            history : [
                {squares: Array(9).fill(null),}
            ],
            XIsNext: true,
            stepNumber: 0,
        }
    }
    handleClick(i){
        const history=this.state.history;
        const squares=history[history.length-1].squares.slice();
        if(squares[i] || calculateWinner(squares)) return;
        squares[i]=(this.state.XIsNext ? 'X': 'O');
        this.setState({
            history: history.concat([
                {squares:squares},
            ]),
            XIsNext: !this.state.XIsNext,
            stepNumber: this.state.history.length,
        });
    }
    jumpTo(move){
        this.setState({
            history: this.state.history.slice(0, move+1),
            stepNumber: move,
            XIsNext: (move==0),
        });
    }
    render(){
        const history=this.state.history;
        const current=history[history.length-1];
        var nextMove="Next move: "+(this.state.XIsNext ? 'X' : 'O');
        let winner=calculateWinner(current.squares);
        const moves=history.map((step, move)=>{
            const desc=move ?
                        "Go to move#"+move :
                        "Go to game start";
            return (
                <li key={move}>
                    <button onClick={()=>this.jumpTo(move)}>
                        {desc}
                    </button>
                </li>
            )
        });
        if(winner){
            nextMove="The winner is "+winner;
        }
        return (
            <div className="game">
                <div className="game-board">
                    <Board squares={current.squares}
                            onClick={(i)=>this.handleClick(i)}/>
                </div>
                <div className="info">{nextMove}</div>
                <ol>{moves}</ol>
            </div>
        )
    }
}

// ========================================

ReactDOM.render( <Game/> ,document.getElementById('root'));
